let allImageCollection=async()=>{let t=await fetch("http://localhost:3000/gallery"),e=await t.json(),a="",l=document.querySelector(".js-collection-container");const n=Math.floor(Math.random()*e[0].images.length);e.forEach(t=>{a+=`\n<div class="item features-image сol-12 col-md-6 col-lg-4">\n                    <div class="item-wrapper">\n                        <div class="item-img">\n                            <a href="easter-festive.html?id=${t.id}"><img src="assets/images/${t.images[n]}"\n                                    alt="${t.alt}" title="" data-slide-to="1"></a>\n                        </div>\n                        <div class="item-content">\n                            <h5 class="item-title mbr-fonts-style display-7"><strong>${t.title}</strong></h5>\n\n\n                        </div>\n\n                    </div>\n                </div>`,l.innerHTML=a})};window.addEventListener("DOMContentLoaded",()=>allImageCollection());