const id=new URLSearchParams(window.location.search).get("id");console.log(id);const blogHeader=document.querySelector(".headerText"),blogBody=document.querySelector(".bodyText"),renderDetails=async()=>{const e=await fetch("https://tboss09.github.io/data/blogs.json"),t=await e.json();t.forEach(e=>{if(e.id==id){const t=`<div class="title col-md-12 col-lg-12">\n                    <h3 class="mbr-section-title mbr-fonts-style align-center mb-4 display-2">\n                        <strong>${e.title}</strong></h3>\n                </div>`,o=`<h3 class="mbr-section-title mbr-fonts-style mb-2 display-7">\n        <em>By ${e.author}</em>\n                    <br> <small>${e.date}</small></h3>\n                    <br>\n                    <p class="mbr-text mbr-fonts-style display-7">${e.body}<br></p>`;blogHeader.innerHTML=t,blogBody.innerHTML=o}})};window.addEventListener("DOMContentLoaded",()=>renderDetails());