const id=new URLSearchParams(window.location.search).get("id"),renderDetails=async()=>{const t=await fetch(`http://localhost:3000/blogs/${id} `),e=await t.json(),n=document.querySelector(".headerText"),s=document.querySelector(".bodyText"),l=`<div class="title col-md-12 col-lg-12">\n                    <h3 class="mbr-section-title mbr-fonts-style align-center mb-4 display-2">\n                        <strong>${e.title}</strong></h3>\n                </div>`,o=`<h3 class="mbr-section-title mbr-fonts-style mb-2 display-7"><small>By ${e.author}</small>\n                    <br> ${e.author}</h3>\n                    <p class="mbr-text mbr-fonts-style display-7">${e.body}<br></p>\n                `;n.innerHTML=l,s.innerHTML=o};window.addEventListener("DOMContentLoaded",()=>renderDetails());